import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{o as s,c as a,f as p,a as t}from"./app-a89b456a.js";const e="/assets/watermark_type_ZmFuZ3poZW5naGVpdGk_shadow_10_text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3hpYW9tdWNnd2xteA___size_16_color_FFFFFF_t_70-1cdc398e.png",o="/assets/插入排序-78edb4c2.gif",c="/assets/image-20230722093327556-47e4e971.png",i="/assets/shell_sort-f272295e.png",l="/assets/简单选择排序-6bcf3cc1.gif",u="/assets/堆排序-cc35da66.gif",r="/assets/冒泡排序-201f48ca.gif",d="/assets/快速排序-e1c33863.gif",k="data:image/png;base64,",v="data:image/png;base64,",m="data:image/png;base64,",A="data:image/png;base64,",b="/assets/归并排序-db4a90c3.gif",g={},w=t('<h2 id="定义" tabindex="-1"><a class="header-anchor" href="#定义" aria-hidden="true">#</a> 定义</h2><p><strong>通过比较来决定元素间的相对次序，由于其时间复杂度不能突破O(nlogn)，因此也称为非线性时间比较类排序。</strong></p><h2 id="复杂度对比" tabindex="-1"><a class="header-anchor" href="#复杂度对比" aria-hidden="true">#</a> 复杂度对比</h2><p>​ <img src="'+e+'" alt="img" style="zoom:70%;"></p><h2 id="插入排序" tabindex="-1"><a class="header-anchor" href="#插入排序" aria-hidden="true">#</a> 插入排序</h2><h3 id="直接插入排序" tabindex="-1"><a class="header-anchor" href="#直接插入排序" aria-hidden="true">#</a> 直接插入排序</h3><p>特点：稳定，适用于顺序存储和链式存储的线性表</p><p>复杂度：时间复杂度为O(n^2)，空间复杂度O(1)</p><p>比较：<strong>比较次数与序列初态有关，为 O(n)~O(n^2)</strong></p><p><strong>步骤：</strong></p><p>​  1.从第一个元素开始，该元素可以认为已经被排序；</p><p>​  2.取出下一个元素，在已经排序的元素序列中<strong>从后向前扫描；</strong></p><p>​  3.<strong>如果扫描到的元素(已排序)大于新元素，将该元素移动到下一个位置；</strong></p><p>​  4.重复步骤 3，直到找到已排序的元素中小于或等于新元素的元素；</p><p>​  5.将新元素插入到该元素的后面；</p><p>​  6.重复步骤 2~5</p><p>​ <img src="'+o+`" style="zoom:75%;"></p><p>​ 代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">insertionSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
  
  <span class="token comment">//从第二个数开始插入，默认第一个数已经有序</span>
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> <span class="token number">1</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
    <span class="token comment">//将要插入的元素前面都是已经有序的，从有序元素的由后往前开始判断</span>
    <span class="token keyword">int</span> preIndex <span class="token operator">=</span> i <span class="token operator">-</span> <span class="token number">1</span><span class="token punctuation">;</span>
    
    <span class="token comment">//current为要插入的元素</span>
    <span class="token keyword">int</span> current <span class="token operator">=</span> arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span>
        
    <span class="token keyword">while</span> <span class="token punctuation">(</span>preIndex <span class="token operator">&gt;=</span> <span class="token number">0</span> <span class="token operator">&amp;&amp;</span> arr<span class="token punctuation">[</span>preIndex <span class="token operator">&gt;</span> current<span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      
      <span class="token comment">//当前元素大于要插入的元素，将当前元素向后移动一个位置</span>
      arr<span class="token punctuation">[</span>preIndex <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>preIndex<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      preIndex <span class="token operator">-=</span> <span class="token number">1</span><span class="token punctuation">;</span>        
    <span class="token punctuation">}</span> 
    
    <span class="token comment">//执行完while循环，说明找到了小于等于新元素的元素，则将新元素插入在该元素后面</span>
    arr<span class="token punctuation">[</span>preIndex <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">=</span> current<span class="token punctuation">;</span>     
  <span class="token punctuation">}</span>    
  <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="折半插入排序" tabindex="-1"><a class="header-anchor" href="#折半插入排序" aria-hidden="true">#</a> 折半插入排序</h3><p>特点：稳定，适用于顺序表</p><p>复杂度：时间复杂度O(n2)</p><p>比较：<strong>比较次数与序列初态无关，取决于元素个数n，为O(nlog2n)；</strong> 当low&gt;hight时，停止比较。</p><h3 id="希尔排序" tabindex="-1"><a class="header-anchor" href="#希尔排序" aria-hidden="true">#</a> 希尔排序</h3><p>特点：不稳定，适用于线性表的顺序存储</p><p>复杂度：平均时间复杂度：O(Nlog2N) 空间复杂度：O(1)</p><p>思想：先将整个待排序的记录序列分割成为若干子序列(由一些相隔某个&quot;增量&quot;的元素组成)<strong>分别进行直接插入排序</strong>，</p><p>​   然后依次缩减增量再进行排序，待整个序列中的元素基本有序(增量足够小)时，再对全体元素进行一次直接插入排序。</p><p>​   直接插入排序在元素基本有序的情况下（接近最好情况），效率是很高的。</p><p>例：</p><p>​ <img src="`+c+'" alt="image-20230722093327556" loading="lazy"></p><p><strong>步骤：</strong></p><p>​  ① 先取一个小于n的整数d1作为第一个增量，把全部记录分成d1个组。</p><p>​  ② 所有距离为d1的倍数的记录放在同一个组中，在各组内进行直接插入排序。</p><p>​  ③ 取第二个增量d2小于d1重复上述的分组和排序，直至所取的增量dt=1，即所有记录放在同一组中进行直接插入排序为止。</p><p>​ 先将整个待排序的记录序列分割成为若干子序列分别进行直接插入排序，具体算法描述：</p><ul><li><p>选择一个增量序列 <code>{t1, t2, …, tk}</code>，其中 <code>(ti&gt;tj, i&lt;j, tk=1)</code>；</p></li><li><p>按增量序列个数 k，对序列进行 k 趟排序；</p></li><li><p>每趟排序，根据对应的增量 <code>t</code>，将待排序列分割成若干长度为 <code>m</code> 的子序列，分别对各子表进行直接插入排序。</p><p>当增量因子为 1 时，整个序列作为一个表来处理，表长度即为整个序列的长度</p></li></ul><p>​ <img src="'+i+`" alt="shell_sort" style="zoom:50%;"></p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span>  <span class="token function">insertionSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span><span class="token punctuation">{</span>
    
  <span class="token keyword">if</span><span class="token punctuation">(</span>arr <span class="token operator">==</span> <span class="token keyword">null</span> <span class="token operator">||</span> arr<span class="token punctuation">.</span>length <span class="token operator">&lt;=</span> <span class="token number">1</span><span class="token punctuation">)</span>
        
    <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
        
  <span class="token comment">//升序排序 d为增量，每轮将其缩小一半，直到等于1 </span>
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> d <span class="token operator">=</span> arr<span class="token punctuation">.</span>length<span class="token operator">/</span><span class="token number">2</span><span class="token punctuation">;</span>d<span class="token operator">&gt;</span><span class="token number">0</span><span class="token punctuation">;</span>d<span class="token operator">/=</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">{</span> 
    
    <span class="token comment">//相当于跳跃排序</span>
    <span class="token comment">//例，假设d=3，数组元素共有14个，i的取值为3到13，</span>
    <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> d<span class="token punctuation">;</span> i <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span><span class="token punctuation">{</span> 
            
      <span class="token comment">//前三轮for循环中，i的取值为3,4,5，分别和数组中下标为0,1,2的元素进行比较并交换</span>
      <span class="token comment">//在第四轮中，i=6，则j=3，即，下标为6和3的元素进行比较，然后j-3=0，即，下标0和3的元素再次进行了比较</span>
      <span class="token comment">//此时，下标为0、3、6的三个元素大小应该是递增的，</span>
      <span class="token comment">//在第五轮中，i=7，则j=4，即下标为7和4的元素进行比较，然后j-3=1，即，下标为1和4的元素进行了比较</span>
      <span class="token comment">//此时，下标为1、4、7的三个元素的大小应该是递增的</span>
      <span class="token comment">//以此类推，i代表着想要插入的元素，在每个子序列内部是使用直接插入排序的</span>
      <span class="token comment">//所以j需要从后往前进行遍历</span>
      <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> j <span class="token operator">=</span> i<span class="token operator">-</span>d<span class="token punctuation">;</span> j<span class="token operator">&gt;=</span><span class="token number">0</span><span class="token punctuation">;</span> j<span class="token operator">-=</span>d<span class="token punctuation">)</span><span class="token punctuation">{</span> 
                
        <span class="token keyword">if</span> <span class="token punctuation">(</span>arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">&gt;</span> arr<span class="token punctuation">[</span>j <span class="token operator">+</span> d<span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
                    
          <span class="token comment">//符合条件，插入元素（交换位置）                    </span>
          <span class="token keyword">int</span> temp <span class="token operator">=</span> arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span><span class="token punctuation">;</span>
                    
          arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>j <span class="token operator">+</span> d<span class="token punctuation">]</span><span class="token punctuation">;</span>
                    
          arr<span class="token punctuation">[</span>j <span class="token operator">+</span> d<span class="token punctuation">]</span> <span class="token operator">=</span> temp<span class="token punctuation">;</span>                
        <span class="token punctuation">}</span>            
      <span class="token punctuation">}</span>        
    <span class="token punctuation">}</span>     
  <span class="token punctuation">}</span>    
  <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="选择排序" tabindex="-1"><a class="header-anchor" href="#选择排序" aria-hidden="true">#</a> 选择排序</h2><h3 id="简单选择排序" tabindex="-1"><a class="header-anchor" href="#简单选择排序" aria-hidden="true">#</a> 简单选择排序</h3><p>特点：不稳定</p><p>复杂度：时间复杂度O(n^2) 空间复杂度O(1)</p><p>比较：<strong>元素比较次数与初始状态无关，始终是n(n-1)/2</strong></p><p>移动：<strong>元素的移动次数不会超过3(n-1)次</strong></p><p>思想：首先在未排序序列中找到最小(大)元素，存放到排序序列的起始位置，然后，再从剩余未排序元素中继续寻找</p><p>      最小(大)元素，然后放到已排序序列的末尾。以此类推，直到所有元素均排序完毕。</p><p><strong>步骤：</strong></p><p>​  1.首先在未排序序列中找到最小(大)元素，存放到排序序列的起始位置</p><p>​  2.再从剩余未排序元素中继续寻找最小(大)元素，然后放到已排序序列的末尾。</p><p>​  3.重复第 2 步，直到所有元素均排序完毕。</p><p>​ <img src="`+l+`" style="zoom:90%;"></p><p>代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">selectionSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
  
  <span class="token comment">//也可以理解为：要确定数组第一个位置放置哪个元素，所以将第一个元素和它后面的所有元素进行比较，然后确定第二个位置应该</span>
  <span class="token comment">//放置哪个元素，所以将第二个元素和它后面的所有元素进行比较，以此类推。</span>
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length <span class="token operator">-</span> <span class="token number">1</span><span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
    <span class="token comment">//记录这一轮比较中的最小元素的索引</span>
    <span class="token keyword">int</span> minIndex <span class="token operator">=</span> i<span class="token punctuation">;</span>
        
    <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> j <span class="token operator">=</span> i <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">;</span> j <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">;</span> j<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      <span class="token keyword">if</span> <span class="token punctuation">(</span>arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">&lt;</span> arr<span class="token punctuation">[</span>minIndex<span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
                
        minIndex <span class="token operator">=</span> j<span class="token punctuation">;</span>            
      <span class="token punctuation">}</span>        
    <span class="token punctuation">}</span>
    
    <span class="token comment">//交换位置，即，找到的最小元素和第一个元素交换位置，第二小元素和第二个元素交换位置，以此类推。</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>minIndex <span class="token operator">!=</span> i<span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      <span class="token keyword">int</span> tmp <span class="token operator">=</span> arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>minIndex<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      arr<span class="token punctuation">[</span>minIndex<span class="token punctuation">]</span> <span class="token operator">=</span> tmp<span class="token punctuation">;</span>
        
    <span class="token punctuation">}</span>    
  <span class="token punctuation">}</span>    
  <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="堆排序" tabindex="-1"><a class="header-anchor" href="#堆排序" aria-hidden="true">#</a> 堆排序</h3><p>如果想升序排序就使用大顶堆，反之使用小顶堆。原因是堆顶元素需要交换到序列尾部。</p><p><strong>构建：</strong></p><p>先直接使用线性数组来表示一个堆，由初始的无序序列建成一个堆就需要自底向上从第一个非叶元素开始挨个调整，直到成为一个堆。</p><p><strong>排序：</strong></p><p>首先是将堆顶元素和最后一个元素交换。然后比较当前堆顶元素的左右孩子节点，因为除了当前的堆顶元素，</p><p>左右孩子堆均满足条件，这时需要将当前堆顶元素与左右孩子节点中的较大者(大顶堆)交换，直至叶子节点。</p><p>从一个无序序列建堆的过程就是一个反复筛选的过程。若将此序列看成是一个完全二叉树，则最后一个非终端节点是</p><p>  下标为n/2(向下取整)的元素。</p><p>特点：不稳定</p><p>复杂度：时间复杂度：最佳：O(nlogn)， 最差：O(nlogn)， 平均：O(nlogn) 空间复杂度：O(1)</p><p><strong>步骤：</strong></p><p>​  1.将初始待排序列 <code>(R1, R2, ……, Rn)</code> 构建成大顶堆，此堆为初始的无序区；</p><p>​  2.将堆顶元素 <code>R[1]</code> 与最后一个元素 <code>R[n]</code> 交换，此时得到新的无序区 <code>(R1, R2, ……, Rn-1)</code> 和新的有序区 (Rn)，</p><p>​    且满足 <code>R[1, 2, ……, n-1]&lt;=R[n]</code>；</p><p>​  3.由于交换后新的堆顶 <code>R[1]</code> 可能违反堆的性质，因此需要对当前无序区 <code>(R1, R2, ……, Rn-1)</code> 调整为新堆，然后再次将</p><p>​    R[1]与无序区最后一个元素交换，得到新的无序区 <code>(R1, R2, ……, Rn-2)</code> 和新的有序区 <code>(Rn-1, Rn)</code>。</p><p>​  4.不断重复此过程，直到有序区的元素个数为 <code>n-1</code>，则整个排序过程完成。</p><p>​ <img src="`+u+`" style="zoom:95%;"></p><p>代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token comment">//记录初始数组的长度</span>
<span class="token keyword">static</span> <span class="token keyword">int</span> heapLen<span class="token punctuation">;</span>

<span class="token comment">//交换数组中两个位置的元素</span>
<span class="token keyword">private</span> <span class="token keyword">static</span> <span class="token keyword">void</span> <span class="token function">swap</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">,</span> <span class="token keyword">int</span> i<span class="token punctuation">,</span> <span class="token keyword">int</span> j<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token keyword">int</span> tmp <span class="token operator">=</span> arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span>
    arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span><span class="token punctuation">;</span>
    arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">=</span> tmp<span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token comment">//构造大根堆</span>
<span class="token keyword">private</span> <span class="token keyword">static</span> <span class="token keyword">void</span> <span class="token function">buildMaxHeap</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
  
  <span class="token comment">//因为堆按照完全二叉树的形式存储，所以第一个非叶子结点的下标是len/2-1</span>
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> arr<span class="token punctuation">.</span>length <span class="token operator">/</span> <span class="token number">2</span> <span class="token operator">-</span> <span class="token number">1</span><span class="token punctuation">;</span> i <span class="token operator">&gt;=</span> <span class="token number">0</span><span class="token punctuation">;</span> i<span class="token operator">--</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        
    <span class="token function">heapify</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> i<span class="token punctuation">)</span><span class="token punctuation">;</span>    
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">//调整元素的位置，使其符合堆的定义</span>
<span class="token keyword">private</span> <span class="token keyword">static</span> <span class="token keyword">void</span> <span class="token function">heapify</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">,</span> <span class="token keyword">int</span> i<span class="token punctuation">)</span> <span class="token punctuation">{</span>
  
  <span class="token comment">//求出要调整节点的左右子节点的下标</span>
  <span class="token keyword">int</span> left <span class="token operator">=</span> <span class="token number">2</span> <span class="token operator">*</span> i <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">;</span>
    
  <span class="token keyword">int</span> right <span class="token operator">=</span> <span class="token number">2</span> <span class="token operator">*</span> i <span class="token operator">+</span> <span class="token number">2</span><span class="token punctuation">;</span>
    
  <span class="token keyword">int</span> largest <span class="token operator">=</span> i<span class="token punctuation">;</span>
  
  <span class="token comment">//找到两个子节点中的较大值</span>
  <span class="token keyword">if</span> <span class="token punctuation">(</span>right <span class="token operator">&lt;</span> heapLen <span class="token operator">&amp;&amp;</span> arr<span class="token punctuation">[</span>right<span class="token punctuation">]</span> <span class="token operator">&gt;</span> arr<span class="token punctuation">[</span>largest<span class="token punctuation">]</span><span class="token punctuation">)</span> 
        
    largest <span class="token operator">=</span> right<span class="token punctuation">;</span>    
    
  <span class="token keyword">if</span> <span class="token punctuation">(</span>left <span class="token operator">&lt;</span> heapLen <span class="token operator">&amp;&amp;</span> arr<span class="token punctuation">[</span>left<span class="token punctuation">]</span> <span class="token operator">&gt;</span> arr<span class="token punctuation">[</span>largest<span class="token punctuation">]</span><span class="token punctuation">)</span> 
        
    largest <span class="token operator">=</span> left<span class="token punctuation">;</span>
      
  <span class="token comment">//如果子节点的值比父节点大，则交换两个节点的位置  </span>
  <span class="token keyword">if</span> <span class="token punctuation">(</span>largest <span class="token operator">!=</span> i<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        
    <span class="token function">swap</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> largest<span class="token punctuation">,</span> i<span class="token punctuation">)</span><span class="token punctuation">;</span>
    
    <span class="token comment">//继续调整交换后的节点</span>
    <span class="token function">heapify</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> largest<span class="token punctuation">)</span><span class="token punctuation">;</span>    
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">//堆排序</span>
<span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">heapSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
  <span class="token comment">//堆的大小  </span>
  heapLen <span class="token operator">=</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">;</span>
    
  <span class="token comment">//构建堆    </span>
  <span class="token function">buildMaxHeap</span><span class="token punctuation">(</span>arr<span class="token punctuation">)</span><span class="token punctuation">;</span>
    
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> arr<span class="token punctuation">.</span>length <span class="token operator">-</span> <span class="token number">1</span><span class="token punctuation">;</span> i <span class="token operator">&gt;</span> <span class="token number">0</span><span class="token punctuation">;</span> i<span class="token operator">--</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        
    <span class="token comment">//交换第一个元素和最后一个元素，以此类推     </span>
    <span class="token function">swap</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> i<span class="token punctuation">)</span><span class="token punctuation">;</span>
    
    <span class="token comment">//将长度减1</span>
    heapLen <span class="token operator">-=</span> <span class="token number">1</span><span class="token punctuation">;</span>
        
    <span class="token function">heapify</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">;</span>      
  <span class="token punctuation">}</span>    
  
  <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="交换排序" tabindex="-1"><a class="header-anchor" href="#交换排序" aria-hidden="true">#</a> 交换排序</h2><h3 id="冒泡排序" tabindex="-1"><a class="header-anchor" href="#冒泡排序" aria-hidden="true">#</a> 冒泡排序</h3><p>特点：稳定</p><p>复杂度：时间复杂度O(n^2) (最好为O(n)，最坏为O(n^2) ) 空间复杂度O(1)：</p><p>比较：第i趟排序要进行n-i次关键字的比较，<strong>每趟排序都会将一个元素放到其最终的位置上</strong></p><p>      <strong>若在一次冒泡中没有发生元素交换，则算法结束</strong>。</p><p><strong>步骤：</strong></p><p>​ 1.比较相邻的元素。如果第一个比第二个大，就交换它们两个；</p><p>​ 2.对每一对相邻元素作同样的工作，从开始第一对到结尾的最后一对，这样在最后的元素应该会是最大的数；</p><p>​ 3.针对所有的元素重复以上的步骤，除了最后一个；</p><p>​ 4.重复步骤 1~3，直到排序完成。</p><p>​ <img src="`+r+`" style="zoom:95%;"></p><p>代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">bubbleSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
  <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> <span class="token number">1</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
                    
    <span class="token keyword">boolean</span> flag <span class="token operator">=</span> <span class="token boolean">true</span><span class="token punctuation">;</span>
    
    <span class="token comment">//第一轮冒泡j会取到倒数第二个元素的下标，第二轮冒泡，j会取到倒数第三个元素的下标</span>
    <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> j <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> j <span class="token operator">&lt;</span> arr<span class="token punctuation">.</span>length <span class="token operator">-</span> i<span class="token punctuation">;</span> j<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      
      <span class="token comment">//如果前一个元素大于后一个元素，则交换位置，每次冒泡都能找到剩余元素中最大的那个</span>
      <span class="token keyword">if</span> <span class="token punctuation">(</span>arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">&gt;</span> arr<span class="token punctuation">[</span>j <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
                
        <span class="token keyword">int</span> tmp <span class="token operator">=</span> arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span><span class="token punctuation">;</span>
                
        arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>j <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
                
        arr<span class="token punctuation">[</span>j <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">=</span> tmp<span class="token punctuation">;</span>
       
        <span class="token comment">//如果发生了交换，则将标志置为false              </span>
        flag <span class="token operator">=</span> <span class="token boolean">false</span><span class="token punctuation">;</span>            
      <span class="token punctuation">}</span>        
    <span class="token punctuation">}</span>
    
    <span class="token comment">//如果此次冒泡没有发生交换，则说明所有的元素都已经有序，则可以直接结束</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>flag<span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      <span class="token keyword">break</span><span class="token punctuation">;</span>        
    <span class="token punctuation">}</span>    
  <span class="token punctuation">}</span>
    
  <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="快速排序" tabindex="-1"><a class="header-anchor" href="#快速排序" aria-hidden="true">#</a> 快速排序</h3><p>基本思想：</p><p>  通过一趟排序将待排序列分隔成独立的两部分，其中一部分记录的元素均比另一部分的元素小，则可分别对这两部分</p><p>  子序列继续进行排序，以达到整个序列有序。</p><p>特点：</p><p>​  不稳定</p><p>​  需要调用一个递归工作栈，平均情况下，栈的深度为O(nlog2n)</p><p>​  递归次数与各元素的初始排列有关，与划分分区后的处理顺序无关</p><p>​  不产生有序子序列;</p><p>​  每趟排序后会将基准元素放到其最终的位置上，第i趟完成时，会有i个以上的元素出现在它最终的位置上</p><p>比较次数：最好: an = a⌊(n-1/2)⌋+a⌈(n-1/2)⌉ + n – 1 (a1=0 a2=1 a3=2)   最差:n*(n-1)/2</p><p>复杂度：时间复杂度：最佳：O(nlogn)， 最差：O(nlogn)，平均：O(nlogn) 空间复杂度：O(logn)</p><p><strong>步骤：</strong></p><p>快速排序使用分治法(Divide and conquer)策略来把一个序列分为较小和较大的 2 个子序列，然后递回地排序两个子序列。</p><p>具体算法描述如下:</p><ol><li><p>从序列中挑出一个元素，做为&quot;基准&quot;(<code>pivot</code>)元素；</p></li><li><p>重新排列序列，将所有比基准元素小的元素放在基准元素前面，所有比基准元素大的放在基准元素的后面(相同的数</p><p>可以到任一边)。在这个操作结束之后，该基准元素就处于数列的中间位置。这个称为分区（partition）操作；</p></li><li><p>递归地把小于基准元素的子序列和大于基准元素的子序列进行快速排序。</p></li></ol><p>​ <img src="`+d+`" style="zoom:85%;"></p><p>代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">void</span> <span class="token class-name">Quick_Sort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">,</span> <span class="token keyword">int</span> begin<span class="token punctuation">,</span> <span class="token keyword">int</span> end<span class="token punctuation">)</span><span class="token punctuation">{</span>
        
  <span class="token keyword">if</span><span class="token punctuation">(</span>begin <span class="token operator">&gt;</span> end<span class="token punctuation">)</span>
            
    <span class="token keyword">return</span><span class="token punctuation">;</span>
        
  <span class="token comment">//以要排序数组的第一个元素为基准元素        </span>
  <span class="token keyword">int</span> tmp <span class="token operator">=</span> arr<span class="token punctuation">[</span>begin<span class="token punctuation">]</span><span class="token punctuation">;</span>
        
  <span class="token comment">//i和j为双指针，i从前往后，寻找大于tmp的元素，j从后往前，寻找小于tmp的元素        </span>
  <span class="token keyword">int</span> i <span class="token operator">=</span> begin<span class="token punctuation">;</span>
        
  <span class="token keyword">int</span> j <span class="token operator">=</span> end<span class="token punctuation">;</span>
        
  <span class="token keyword">while</span><span class="token punctuation">(</span>i <span class="token operator">!=</span> j<span class="token punctuation">)</span><span class="token punctuation">{</span>
            
    <span class="token comment">//每次都要j先移动 找到小于tmp的元素时停止            </span>
    <span class="token keyword">while</span><span class="token punctuation">(</span>arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">&gt;=</span> tmp <span class="token operator">&amp;&amp;</span> j <span class="token operator">&gt;</span> i<span class="token punctuation">)</span>
                
      j<span class="token operator">--</span><span class="token punctuation">;</span>
            
    <span class="token comment">//j找到小于tmp的元素后，i再向后寻找大于tmp的元素            </span>
    <span class="token keyword">while</span><span class="token punctuation">(</span>arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">&lt;=</span> tmp <span class="token operator">&amp;&amp;</span> j <span class="token operator">&gt;</span> i<span class="token punctuation">)</span>
                
      i<span class="token operator">++</span><span class="token punctuation">;</span>
            
    <span class="token comment">//两个元素都找到后，交换两个元素            </span>
    <span class="token keyword">if</span><span class="token punctuation">(</span>j <span class="token operator">&gt;</span> i<span class="token punctuation">)</span><span class="token punctuation">{</span>
                
      <span class="token keyword">int</span> t <span class="token operator">=</span> arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span>
                
      arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span><span class="token punctuation">;</span>
                
      arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">=</span> t<span class="token punctuation">;</span>            
    <span class="token punctuation">}</span>        
  <span class="token punctuation">}</span>
        
  <span class="token comment">//当i等于j时退出循环，此时i和j的位置就是tmp应该存放的位置        </span>
  arr<span class="token punctuation">[</span>begin<span class="token punctuation">]</span> <span class="token operator">=</span> arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span>
        
  arr<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> tmp<span class="token punctuation">;</span>
        
  <span class="token comment">//递归       </span>
  <span class="token class-name">Quick_Sort</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> begin<span class="token punctuation">,</span> i<span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        
  <span class="token class-name">Quick_Sort</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> i<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">,</span> end<span class="token punctuation">)</span><span class="token punctuation">;</span>    
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>例：</p><p>假设对以下10个数进行快速排序：6,1,2,7,9,3,4,5,10,8</p><ol><li><p>在这个序列中随便找一个数作为基准数，通常为了方便，以第一个数作为基准数。</p><p>在初始状态下，数字6在序列的第1位。目标是寻找到位置k，以第k位为分界点，左边的数都小于等于6，右边的数都</p><p>大于等于6，将6挪到k的位置。</p></li><li><p>快速排序是分别从两端开始”探测”的，先从右往左找一个小于6的数，再从左往右找一个大于6的数，然后交换他们。</p><p>这里可以用两个变量 i 和 j，分别指向序列最左边和最右边。</p></li></ol><p>​ <img src="`+k+'" alt="image-20230722183137493" loading="lazy"></p><ol start="3"><li><p>哨兵 j 一步一步地向左挪动(即 j = j − 1 )，直到找到一个小于6的数停下来。接下来哨兵 i 再一步一步向右挪动</p><p>(即i = i + 1)，直到找到一个数大于6的数停下来。最后哨兵j停在了数字5面前，哨兵 i 停在了数字7面前，</p><p>交换i和j所指向的元素。</p></li></ol><p>​ <img src="'+v+'" alt="image-20230722183343959" loading="lazy"></p><ol start="4"><li>i和j再次出发，再找到符合的元素并交换。</li></ol><p>​ <img src="'+m+'" alt="image-20230722183458923" loading="lazy"></p><ol start="5"><li>然后i和j再次移动，直到在元素3的位置相遇，则此处就是基准元素6应该存放的位置。</li></ol><p>​ <img src="'+A+'" alt="image-20230722183658588" loading="lazy"></p><ol start="6"><li>然后再递归排序基准元素两边的元素。</li></ol><h3 id="归并排序" tabindex="-1"><a class="header-anchor" href="#归并排序" aria-hidden="true">#</a> 归并排序</h3><p>归并排序是建立在归并操作上的一种有效的排序算法。该算法是采用分治法(Divide and Conquer)的一个非常典型的应用。</p><p>归并排序是一种稳定的排序方法。将已有序的子序列合并，得到完全有序的序列；即先使每个子序列有序，再使子序列段间有序。</p><p>若将两个有序表合并成一个有序表，称为 2 - 路归并。</p><p>归并排序的性能不受输入数据的影响，但表现比选择排序好的多，因为始终都是 <code>O(nlogn)</code> 的时间复杂度。代价是需要额外的内存空间。</p><p>特点：稳定</p><p>复杂度：时间复杂度为 O(nlog2n) 空间复杂度O(n)</p><p>比较：每趟归并的时间复杂度为O(n)，共需进行log2n(取上限)(二路)趟归并</p><p><strong>一般而言，对N个元素进行k路归并排序时，排序的趟数m=logkN (取上限)</strong></p><p>步骤：</p><p>​ 归并排序算法是一个递归过程，<strong>边界条件为当输入序列仅有一个元素时，直接返回</strong>，具体过程如下：</p><ol><li><p>如果数组内只有一个元素，则直接返回，否则将长度为 <code>n</code> 的输入序列分成两个长度为 <code>n/2</code> 的子序列；</p></li><li><p>分别对这两个子序列进行归并排序，使子序列变为有序状态；</p></li><li><p>设定两个指针，分别指向两个已经排好序的子序列的起始位置；</p></li><li><p>比较两个指针所指向的元素，选择相对小的元素放入到合并空间（用于存放排序结果），并移动指针到下一位置；</p></li><li><p>重复步骤 3~4 直到某一指针达到序列尾；</p></li><li><p>将另一序列剩下的所有元素直接复制到合并序列的尾部</p></li></ol><p>​ <img src="'+b+`" style="zoom:75%;"></p><p>代码：</p><div class="language-java line-numbers-mode" data-ext="java"><pre class="language-java"><code><span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">mergeSort</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
  <span class="token keyword">if</span> <span class="token punctuation">(</span>arr<span class="token punctuation">.</span>length <span class="token operator">&lt;=</span> <span class="token number">1</span><span class="token punctuation">)</span> 
        
    <span class="token keyword">return</span> arr<span class="token punctuation">;</span>
    
      
  <span class="token keyword">int</span> middle <span class="token operator">=</span> arr<span class="token punctuation">.</span>length <span class="token operator">/</span> <span class="token number">2</span><span class="token punctuation">;</span>
  
  <span class="token comment">//将原数组分为两个子数组</span>
  <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr_1 <span class="token operator">=</span> <span class="token class-name">Arrays</span><span class="token punctuation">.</span><span class="token function">copyOfRange</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> middle<span class="token punctuation">)</span><span class="token punctuation">;</span>
    
  <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr_2 <span class="token operator">=</span> <span class="token class-name">Arrays</span><span class="token punctuation">.</span><span class="token function">copyOfRange</span><span class="token punctuation">(</span>arr<span class="token punctuation">,</span> middle<span class="token punctuation">,</span> arr<span class="token punctuation">.</span>length<span class="token punctuation">)</span><span class="token punctuation">;</span>
  
  <span class="token comment">//递归调用归并算法</span>
  <span class="token keyword">return</span> <span class="token function">merge</span><span class="token punctuation">(</span><span class="token function">mergeSort</span><span class="token punctuation">(</span>arr_1<span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token function">mergeSort</span><span class="token punctuation">(</span>arr_2<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token comment">//合并两个数组</span>
<span class="token keyword">public</span> <span class="token keyword">static</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">merge</span><span class="token punctuation">(</span><span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr_1<span class="token punctuation">,</span> <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> arr_2<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    
  <span class="token keyword">int</span><span class="token punctuation">[</span><span class="token punctuation">]</span> sorted_arr <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token keyword">int</span><span class="token punctuation">[</span>arr_1<span class="token punctuation">.</span>length <span class="token operator">+</span> arr_2<span class="token punctuation">.</span>length<span class="token punctuation">]</span><span class="token punctuation">;</span>
    
  <span class="token keyword">int</span> idx <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">,</span> idx_1 <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">,</span> idx_2 <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span>
    
  <span class="token keyword">while</span> <span class="token punctuation">(</span>idx_1 <span class="token operator">&lt;</span> arr_1<span class="token punctuation">.</span>length <span class="token operator">&amp;&amp;</span> idx_2 <span class="token operator">&lt;</span> arr_2<span class="token punctuation">.</span>length<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        
    <span class="token keyword">if</span> <span class="token punctuation">(</span>arr_1<span class="token punctuation">[</span>idx_1<span class="token punctuation">]</span> <span class="token operator">&lt;</span> arr_2<span class="token punctuation">[</span>idx_2<span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      sorted_arr<span class="token punctuation">[</span>idx<span class="token punctuation">]</span> <span class="token operator">=</span> arr_1<span class="token punctuation">[</span>idx_1<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      idx_1 <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>
        
    <span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token punctuation">{</span>
            
      sorted_arr<span class="token punctuation">[</span>idx<span class="token punctuation">]</span> <span class="token operator">=</span> arr_2<span class="token punctuation">[</span>idx_2<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      idx_2 <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>        
    <span class="token punctuation">}</span>
        
    idx <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>    
  <span class="token punctuation">}</span>
    
  <span class="token keyword">if</span> <span class="token punctuation">(</span>idx_1 <span class="token operator">&lt;</span> arr_1<span class="token punctuation">.</span>length<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        
    <span class="token keyword">while</span> <span class="token punctuation">(</span>idx_1 <span class="token operator">&lt;</span> arr_1<span class="token punctuation">.</span>length<span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      sorted_arr<span class="token punctuation">[</span>idx<span class="token punctuation">]</span> <span class="token operator">=</span> arr_1<span class="token punctuation">[</span>idx_1<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      idx_1 <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>
            
      idx <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>        
    <span class="token punctuation">}</span>
    
  <span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token punctuation">{</span>
        
    <span class="token keyword">while</span> <span class="token punctuation">(</span>idx_2 <span class="token operator">&lt;</span> arr_2<span class="token punctuation">.</span>length<span class="token punctuation">)</span> <span class="token punctuation">{</span>
            
      sorted_arr<span class="token punctuation">[</span>idx<span class="token punctuation">]</span> <span class="token operator">=</span> arr_2<span class="token punctuation">[</span>idx_2<span class="token punctuation">]</span><span class="token punctuation">;</span>
            
      idx_2 <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>
            
      idx <span class="token operator">+=</span> <span class="token number">1</span><span class="token punctuation">;</span>        
    <span class="token punctuation">}</span>
    
  <span class="token punctuation">}</span>
    
  <span class="token keyword">return</span> sorted_arr<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a></h3>`,135);function y(h,f){return s(),a("div",null,[p(" permalink: /面试/数据结构/    "),w])}const x=n(g,[["render",y],["__file","比较类排序.html.vue"]]);export{x as default};
